import React, { useContext } from 'react'
import { SearchContext } from '../../context/searchContext'
import yoga from '../images/yoga.jpg'
import girl from '../images/girl.jpg'
import './movie.css'

const Card = () => {
    const {movies, loading} = useContext(SearchContext)
    const data = [
        {
            id : 1,
            img: yoga,
            title: "Defiance",
            desc: "One of the best scifi tv shows ever made"
        },
        {
            id : 2,
            img: girl,
            title: "The Expanse",
            desc: "The best space opera ever made, The best space opera ever made"
        },
        {
            id : 3,
            img: girl,
            title: "Star Trek lower decks",
            desc: "A star trek movie"
        },
        {
            id : 4,
            img: girl,
            title: "The Expanse",
            desc: "The best space opera ever made."
        },
        {
            id : 5,
            img: girl,
            title: "Star Trek lower decks",
            desc: "A star trek movie"
        }

    ]


  return (
      <>
        {loading && <p>Hold on one minute...</p>}
        {
        movies.map((data)=>(
            <div className="card">
            <div className="card-image">
                <img src={data.img} alt="" />
            </div>
            <div className="card-text">
                <h4 className="card-title">{data.title}</h4>
                <p className="card-description">{data.desc}</p>
            </div>
            </div>
        ))      
        }  
      </>
  );
};

export default Card;
